				{% set _ = bill.append({"date": bed.check_in, "category": category, "item_name": item_name , "quantity": 1,  'item_rate': item_rate, "amount": amount }) %}
			{% endfor %}

			{##### IPD BED CONS #####}
			{% set ipd_cons = frappe.db.get_all("Inpatient Consultancy", filters={'parent': inpatient_id}, fields=['date', 'consultation_item','rate'], order_by='date asc') %}
			{% for ipdc in ipd_cons %}
				{% set category = frappe.db.get_value('Item', ipdc.consultation_item, 'item_group') %}
				{% set item_uom = frappe.db.get_value('Item', ipdc.consultation_item, 'stock_uom') %}
				{% set item_rate = get_item_rate(ipdc.consultation_item, doc.company, doc.insurance_subscription) %}
				{% set amount = item_rate * 1 %}
				{% set _ = bill.append({"date": ipdc.date, "category": category, "item_name": ipdc.consultation_item , "quantity": 1,  'item_rate': item_rate, "amount": amount }) %}
			{% endfor %}
		{% endif %}
		
		
		<TABLE border=1 style="width: 100%; border-collapse: collapse; border: 1px grey; margin-bottom: 10px;">
			<COLGROUP>
				<COL WIDTH="5%">
				<COL WIDTH="11%">
				<COL WIDTH="25%">
				<COL WIDTH="36%">
				<COL WIDTH="5%">
				<COL WIDTH="8%">
				<COL WIDTH="15%">		
			</COLGROUP>
			<THEAD style="background-color: darkgray;">
				<TR>
					<TH style="text-align: center;">&nbsp;</TH>
					<TH style="text-align: center;">Date</TH>
					<TH style="text-align: center;">Category</TH>
					<TH style="text-align: center;">Description</TH>
					<TH style="text-align: center;">Qty</TH>
					<TH style="text-align: center;">Rate</TH>
					<TH style="text-align: center;">Amount</TH>				
				</TR>
			</THEAD>
			<TBODY>
				{% set total = [0] %}
				{% for item in bill %}
					{% set total = total.append(total.pop() + item.amount) %}
					<TR>
						<TD style="text-align: center;">{{ loop.index }}</TD>
						<TD style="text-align: center;"> {{frappe.utils.formatdate(item.date, "dd-MMM-YYYY") }}</TD>
						<TD style="text-align: left;">{{ item.category }}</TD>
						<TD style="text-align: left;">{{ item.item_name }}</TD>
						<TD style="text-align: center;">{{ item.quantity|int }}</TD>
						<TD style="text-align: right;">{{ frappe.utils.fmt_money(item.item_rate, precision=0) }}</TD>
						<TD style="text-align: right;">{{ frappe.utils.fmt_money(item.amount, precision=0) }}</TD>
					</TR>
				{% endfor %}
			</TBODY>
			<TFOOT>
				<TR>
					<TH style="text-align: center;">&nbsp;</TH>
					<TH style="text-align: left;" colspan="5">TOTAL</TH>
					<TH style="text-align: right;">{{ frappe.utils.fmt_money(total[0], precision=0) }}</TH>			
				</TR> 
			</TFOOT>
		</TABLE>
		
	{% else %}
		This appointment is not closed
	{% endif %}
{% endif %}
